package com.MITProject.TravelerHelperBot.botController;import com.MITProject.TravelerHelperBot.auth.JwtUtil;import com.MITProjectService.bot.Services.BookingService;import com.MITProjectService.bot.domain.Bookings;import com.MITProjectService.bot.request.BookingRequest;import jakarta.servlet.http.HttpServletRequest;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;import java.util.Optional;@Slf4j@RestController@RequestMapping(path = "app/user/category")@RequiredArgsConstructorpublic class BookingController {    private final BookingService bookingService;    @PostMapping(value = "/submitBooking")    public ResponseEntity<Bookings> submitBooking(@Valid @RequestBody BookingRequest request, HttpServletRequest httpServletRequest) {        Optional<String> headerToken = Optional.of(httpServletRequest.getHeader("Authorization"));        String token = headerToken.get().replace("Bearer ", "");        Optional<String> userId = Optional.of(JwtUtil.getTelegramIdFromToken(token));        Bookings booking = bookingService.submitBooking(request);        return ResponseEntity.ok(booking);    }}