package com.MITProject.TravelerHelperBot.botService;import org.springframework.stereotype.Component;import org.telegram.telegrambots.meta.api.methods.send.SendAnimation;import org.telegram.telegrambots.meta.api.methods.send.SendMessage;import org.telegram.telegrambots.meta.api.methods.send.SendPhoto;import org.telegram.telegrambots.meta.api.objects.replykeyboard.InlineKeyboardMarkup;@Componentpublic class TelegramBotChats {    SendMessage sendMessage = new SendMessage();    SendPhoto sendPhoto = new SendPhoto();    SendAnimation sendAnimation = new SendAnimation();    InlineKeyboardMarkup inlineKeyboardMarkup = new InlineKeyboardMarkup();    public SendMessage sendWelcomeMessage(Long chatId) {        sendMessage.setChatId(chatId.toString());        sendMessage.setText("Welcome to the Travel Assistant Bot! I'm here to help you with travel planning, bookings, and more. Type /help to see what I can do!");        return sendMessage;    }}