package com.MITProjectAdmin.Controller.system;import com.MITProjectService.admin.domain.system.SysUser;import com.MITProjectService.admin.service.SysUserService;import jakarta.servlet.ServletException;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import jakarta.servlet.http.HttpSession;import org.springframework.security.core.Authentication;import org.springframework.security.web.authentication.AuthenticationSuccessHandler;import org.springframework.stereotype.Controller;import java.io.IOException;@Controllerpublic class CustomAuthenticationSuccessHandler implements AuthenticationSuccessHandler {    private final SysUserService sysUserService;    public CustomAuthenticationSuccessHandler(SysUserService sysUserService) {        this.sysUserService = sysUserService;    }    @Override    public void onAuthenticationSuccess(HttpServletRequest request, HttpServletResponse response, Authentication authentication) throws IOException, ServletException {        System.out.println("In customAuthenticationSuccessHandler");        String userName = authentication.getName();        System.out.println("user NAme: "+ userName);        SysUser user = sysUserService.findByUserName(userName);        HttpSession httpSession = request.getSession();        httpSession.setAttribute("user",user);    }}