package com.MITProjectAdmin.Controller.system;import com.MITProjectService.admin.domain.SysUser;import com.MITProjectService.admin.service.SysUserService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping(path= "/admin/systemUser")public class SystemUserController {    Logger logger = LoggerFactory.getLogger(SystemUserController.class);    @Autowired    public SystemUserController(SysUserService systemUserService) {        this.systemUserService = systemUserService;    }    private final SysUserService systemUserService;    @GetMapping(value = "/login")    public String login(){        return "login";    }    @RequestMapping(value = "/index", method = RequestMethod.POST)    public String index(){        return "index";    }    @PostMapping(value = "/add")    public ResponseEntity<SysUser> addSysUser(@RequestBody SysUser sysUser){        return ResponseEntity.ok(systemUserService.addSysUser(sysUser));    }    @RequestMapping(value = "/list", method = RequestMethod.GET)    public String list(){        return "list";    }}