package com.MITProjectAdmin.Controller.system;import com.MITProjectService.admin.domain.SysUser;import com.MITProjectService.admin.service.impl.SysUserServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/admin/systemUser")public class SystemUserController {    @Autowired    private SysUserServiceImpl systemUserService;    @RequestMapping(value = "/login", method = RequestMethod.POST)    public String login(){        return "login";    }    @RequestMapping(value = "/index", method = RequestMethod.POST)    public String index(){        return "index";    }    @RequestMapping(value = "/add", method = RequestMethod.POST)    public ResponseEntity<SysUser> addSysUser(@RequestBody SysUser sysUser){        return ResponseEntity.ok(systemUserService.addSysUser(sysUser));    }    @RequestMapping(value = "/list", method = RequestMethod.GET)    public String list(){        return "list";    }}