package com.MITProjectAdmin.configs;import com.MITProjectService.admin.domain.system.Authorities;import org.springframework.security.core.GrantedAuthority;public class GrantedAuthorities implements GrantedAuthority {    private final Authorities authorities;    public GrantedAuthorities(Authorities authorities) {        this.authorities = authorities;    }    @Override    public String getAuthority() {        return authorities.getRole();    }}