package com.MITProjectService.admin.dao.JpaRepos.specification;import com.MITProjectService.admin.domain.system.SysUser;import com.MITProjectService.admin.request.SystemUserRequest;import jakarta.persistence.criteria.Predicate;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.util.StringUtils;@Servicepublic class SysUserFilterSpecifications {    public static Specification<SysUser> findAndFilter(SystemUserRequest request) {        return (root, query, cb) -> {            Predicate predicate = cb.conjunction();            if (StringUtils.hasText(request.getUsername())) {                predicate = cb.and(predicate, cb.like(root.get("username"), "%" + request.getUsername() + "%"));            }            if (StringUtils.hasText(request.getEmail())) {                predicate = cb.and(predicate, cb.like(root.get("email"), "%" + request.getEmail() + "%"));            }            return predicate;        };    }}