package com.MITProjectService.admin.dao.impl;import com.MITProjectService.admin.dao.AuthoritiesRepo;import com.MITProjectService.admin.domain.system.Authorities;import jakarta.persistence.EntityManager;import jakarta.persistence.TypedQuery;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.List;@Repositorypublic class AuthoritiesRepoImpl implements AuthoritiesRepo {    private final EntityManager entityManager;    @Autowired    public AuthoritiesRepoImpl(EntityManager entityManager) {        this.entityManager = entityManager;    }    @Override    public void save(Authorities authorities) {        entityManager.persist(authorities);    }    @Override    public List<Authorities> listAuthorities(String userName) {        TypedQuery<Authorities> query = entityManager.createQuery("SELECT a FROM Authorities a WHERE a.userName = :username", Authorities.class);        query.setParameter("username", userName);        List<Authorities> authorities = null;        try {            authorities = query.getResultList();            return authorities;        }catch (Exception e) {            return authorities;        }    }}