package com.MITProjectService.admin.dao.impl;import com.MITProjectService.admin.dao.AuthoritiesRepo;import com.MITProjectService.admin.domain.system.Authorities;import com.MITProjectService.admin.domain.system.SysUser;import jakarta.persistence.EntityManager;import jakarta.persistence.TypedQuery;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Repositorypublic class AuthoritiesRepoImpl implements AuthoritiesRepo {    @Autowired    private EntityManager entityManager;    @Override    public void save(Authorities authorities) {        entityManager.persist(authorities);    }    @Override    public Authorities setAuthorities(String userName) {        TypedQuery<Authorities> query = entityManager.createQuery("FROM authorities WHERE user_name =: user_name ",Authorities.class);        query.setParameter("userName",userName);        Authorities authorities = null;        try {            authorities = query.getSingleResult();            return authorities;        }catch (Exception e) {            return authorities;        }    }}