package com.MITProjectService.admin.dao.impl;import com.MITProjectService.admin.dao.SystemUserRepo;import com.MITProjectService.admin.domain.system.SysUser;import jakarta.persistence.EntityManager;import jakarta.persistence.TypedQuery;import jakarta.transaction.Transactional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Repositorypublic class SystemUserRepoImpl implements SystemUserRepo {    private final EntityManager entityManager;    @Autowired    public SystemUserRepoImpl(EntityManager entityManager) {        this.entityManager = entityManager;    }    @Override    @Transactional    public SysUser save(SysUser sysUser) {        entityManager.persist(sysUser);        return null;    }    @Override    public SysUser findByUserName(String name) {        TypedQuery<SysUser> query = entityManager.createQuery("FROM system_user WHERE username = :userName and status = 1",SysUser.class);        query.setParameter("userName",name);        SysUser sysUser = null;        try {            sysUser = query.getSingleResult();            return sysUser;        }catch (Exception e) {            return sysUser;        }    }}