package com.MITProjectService.admin.domain.system;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.time.Instant;@Data@NoArgsConstructor@AllArgsConstructor@Entity@Table(name = "refresh_token")public class RefreshToken {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false, unique = true)    private String token;    @Column(nullable = false, unique = true)    private String accessToken;    @Column(nullable = false)    private Instant expiryDate;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "user_id", nullable = false)    private SysUser user;}