package com.MITProjectService.admin.domain.system;import lombok.Builder;import org.springframework.data.repository.NoRepositoryBean;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.util.Date;@Builderpublic record SysUserRecord(        @NotBlank(message = "include name ")        String username,        @NotBlank(message = "password cannot be blank")        @Size(min = 6, max = 12, message = "ped must be between 6-12 characters")        String password,        String role,        @NotNull        Integer status,        @Email(message = "invalid email format")        @NotBlank(message = "email cannot be blank")        String email,        String phone,        String address,        String avatar,        Date create_time,        Date update_time) {        public static SysUserRecord createWithDefaultStatus(String username, String password, String role, Integer status, String email, String phone, String address, String avatar, Date create_time, Date update_time) {                int defaultStatus = 1;                Integer finalStatus = (status != null) ? status : defaultStatus;                return new SysUserRecord(username, password, role, finalStatus, email, phone, address, avatar, create_time, update_time);        }}