package com.MITProjectService.admin.domain.system;import jakarta.persistence.*;import lombok.*;// immutable role detail object for transaction@Entity@Table(name = "user_role")@Getter@Setter@ToString@RequiredArgsConstructorpublic class UserRole{    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id", updatable = false, nullable = false)    private Long id;    @Column(name = "role", unique = true, nullable = false)    private String role;    @Column(name = "status", nullable = false, columnDefinition = "INTEGER DEFAULT 1")    private Integer status;    @Column(name = "description")    private String description;    @Column(name = "permissions", columnDefinition = "TEXT")    private String permissions;}