package com.MITProjectService.admin.domain.system;import jakarta.persistence.*;// immutable role detail object for transaction@Entitypublic record UserRole(        @Id        @GeneratedValue(strategy = GenerationType.IDENTITY)        @Column(name = "id")        Long id,        @Column(name = "role",unique = true, nullable = false)        String role,        @Column(name = "status", nullable = false, columnDefinition = "INTEGER DEFAULT 1")        Integer status,        @Column(name = "description")        String description,        @Column(name = "permissions",columnDefinition = "TEXT")        String permissions) {}