package com.MITProjectService.admin.request;import com.MITProjectCommon.constants.Constants;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.Date;@Data@AllArgsConstructor@NoArgsConstructorpublic class SystemUserRequest {    private int page=(int) Constants.DEFAULT_PAGE;    private int limit= (int) Constants.DEFAULT_LIMIT;    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private String id;    @Column(name = "username",unique = true, nullable = false)    private String username;    @Column(name = "password",nullable = false)    private String password;    @Column(name = "role", nullable = false)    private String role;    @Column(name = "status", nullable = false, columnDefinition = "int default 1")    private Integer status;    @Column(name = "email", unique = true)    private String email;    @Column(name = "phone", unique = true)    private String phone;    @Column(name = "address")    private String address;    @Column(name = "avatar")    private String avatar;    @Column(name = "create_time", nullable = false,columnDefinition = "TIMESTAMP DEFAULT CURRENT_TIMESTAMP")    private Date create_time;    @Column(name = "update_time", columnDefinition = "TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP")    private Date update_time;}