package com.MITProjectService.admin.service.impl;import com.MITProjectService.admin.dao.JpaRepos.SysUserRepository;import com.MITProjectService.admin.dao.SystemUserRepo;import com.MITProjectService.admin.domain.system.SysUser;import com.MITProjectService.admin.request.SystemUserRequest;import com.MITProjectService.admin.service.SysUserService;import com.MITProjectService.admin.vo.SystemUserVo;import jakarta.servlet.http.HttpServletRequest;import jakarta.servlet.http.HttpServletResponse;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.web.bind.annotation.RequestBody;import java.util.Date;@Servicepublic class SysUserServiceImpl implements SysUserService {    private final SystemUserRepo systemUserRepo;    private final SysUserRepository sysUserRepository;    @Autowired    public SysUserServiceImpl(SystemUserRepo systemUserRepo, SysUserRepository sysUserRepository) {        this.systemUserRepo = systemUserRepo;        this.sysUserRepository = sysUserRepository;    }    @Override    public SysUser addSysUser(SysUser sysUser) {        sysUser.setCreate_time(new Date());        return systemUserRepo.save(sysUser);    }    @Override    public SysUser findByUserName(String userName) {        return systemUserRepo.findByUserName(userName);    }    @Override    public Page<SysUser> findAll(Specification<SysUser> specification, Pageable pageable) {        return sysUserRepository.findAll(specification,pageable);    }}