package com.MITProjectService.admin.service.impl;import com.MITProjectService.admin.dao.JpaRepos.UserRolesRepo;import com.MITProjectService.admin.domain.system.UserRole;import com.MITProjectService.admin.service.UserRolesService;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.Page;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import org.springframework.data.domain.Pageable;import java.util.Optional;@Servicepublic class UserRolesServiceImpl implements UserRolesService {    @Autowired    UserRolesRepo userRolesRepo;    @Override    @Transactional    public void saveRole(UserRole userRole) {        userRolesRepo.save(userRole);    }    @Override    public Optional<UserRole> getReferenceById(Long id) {        return userRolesRepo.findById(id);    }    @Override    @Transactional    public void updateUserRole(UserRole userRole) {        UserRole update = userRolesRepo.getReferenceById(userRole.getId());        BeanUtils.copyProperties(userRole,update);        userRolesRepo.save(update);    }    @Override    public Page<UserRole> getAllRoles(Pageable pageable) {        return userRolesRepo.findAll(pageable);    }    @Override    public void delete(Long id) {        userRolesRepo.deleteById(id);    }}