package com.MITProjectService.bot.Services;import com.MITProjectService.bot.request.PaymentRequest;public interface PayHereService {    /**     * Processes a payment request by validating user and booking details,     * generating a PayHere payment request, and returning a payment URL.     *     * @param paymentRequest The payment request object containing necessary details such as user ID, booking ID, amount, and currency.     * @return The generated PayHere payment URL where the user can complete the payment.     * @throws RuntimeException If the user or booking is not found or if an error occurs during payment processing.     */    String doPayment(PaymentRequest paymentRequest);}