package com.MITProjectService.bot.Services.impl;import com.MITProjectService.bot.Services.PaymentLogService;import com.MITProjectService.bot.dao.jpaRepos.PaymentLogsRepository;import com.MITProjectService.bot.domain.PaymentLogs;import jakarta.annotation.Resource;import org.springframework.stereotype.Service;@Servicepublic class PaymentLogServiceImpl implements PaymentLogService {    @Resource    private PaymentLogsRepository paymentLogsRepository;    /**     * Saves a payment log entry to the database.     *     * @param paymentLogs The {@link PaymentLogs} object containing transaction details.     * @throws IllegalArgumentException If the provided {@code paymentLogs} object is {@code null}.     */    @Override    public void save(PaymentLogs paymentLogs) {        if (paymentLogs == null) {            throw new IllegalArgumentException("PaymentLogs object cannot be null");        }        paymentLogsRepository.save(paymentLogs);    }}