package com.MITProjectService.bot.Services.impl;import com.MITProjectCommon.common.OrderIdGenerator;import com.MITProjectService.bot.Services.PayHereService;import com.MITProjectService.bot.Services.PaymentService;import com.MITProjectService.bot.dao.jpaRepos.PayHereRepository;import com.MITProjectService.bot.dao.jpaRepos.PaymentRepository;import com.MITProjectService.bot.domain.Payment;import com.MITProjectService.bot.enums.PaymentMethodEnum;import com.MITProjectService.bot.request.PaymentRequest;import jakarta.annotation.Resource;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class PaymentServiceImpl implements PaymentService {    @Resource    private PaymentRepository paymentRepository;    @Resource    private PayHereService payHereService;    @Autowired    private PayHereRepository payHereRepository;    @Override    public String pay(PaymentRequest paymentRequest) {        return switch (PaymentMethodEnum.fromName(paymentRequest.getPaymentMethod())) {            case CASH -> "false";            case ONLINE -> "false";            case CARD -> payHereService.doPayment(paymentRequest);            default -> "false";        };    }    @Override    public Payment saveOrUpdate(Payment payment) {        return paymentRepository.save(payment);    }    @Override    public Payment findByBookingId(Long bookingId) {        return paymentRepository.getByBookingId(bookingId);    }}