package com.MITProjectService.bot.Services.impl;import com.MITProjectService.bot.Services.ServiceProviderService;import com.MITProjectService.bot.dao.jpaRepos.ServiceProviderRepository;import com.MITProjectService.bot.dao.jpaRepos.specification.SnServiceProviderFilterSpecification;import com.MITProjectService.bot.domain.ServiceProvider;import lombok.NonNull;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.Optional;@Servicepublic class ServiceProviderServiceImpl implements ServiceProviderService {    public ServiceProviderServiceImpl(ServiceProviderRepository serviceProviderRepository) {        this.serviceProviderRepository = serviceProviderRepository;    }    private final ServiceProviderRepository serviceProviderRepository;    @Override    @Transactional    public void saveServiceProvider(ServiceProvider serviceProvider) {        serviceProviderRepository.save(serviceProvider);    }    @Override    public Page<ServiceProvider> getAllServiceProvider(Specification<ServiceProvider> filter, Pageable pageable) {        return serviceProviderRepository.findAll(filter, pageable);    }    @Override    public boolean getAServiceProvider(@NonNull Long telegramId) {        Specification<ServiceProvider> spec = SnServiceProviderFilterSpecification.hasTelegramId(telegramId);        Optional<ServiceProvider> isServiceProvider = serviceProviderRepository.findOne(spec);        return isServiceProvider.isPresent();    }    @Override    public Optional<ServiceProvider> getUserByID(Long id) {        return serviceProviderRepository.findById(id);    }    @Override    @Transactional    public void deleteServiceProvider(Long id) {        serviceProviderRepository.deleteById(id);    }    @Override    @Transactional    public ServiceProvider updateServiceProvider(ServiceProvider request) {        return null;    }}