package com.MITProjectService.bot.Services.impl;import com.MITProjectService.bot.Services.ServiceProviderServicer;import com.MITProjectService.bot.dao.jpaRepos.ServiceProviderRepository;import com.MITProjectService.bot.domain.ServiceProvider;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Servicepublic class ServiceProviderServiceImpl implements ServiceProviderServicer {    public ServiceProviderServiceImpl(ServiceProviderRepository serviceProviderRepository) {        this.serviceProviderRepository = serviceProviderRepository;    }    private final ServiceProviderRepository serviceProviderRepository;    @Override    @Transactional    public void saveServiceProvider(ServiceProvider serviceProvider) {        serviceProviderRepository.save(serviceProvider);    }}