package com.MITProjectService.bot.Services.impl;import com.MITProjectService.bot.Services.ServicesService;import com.MITProjectService.bot.dao.jpaRepos.ServicesRepository;import com.MITProjectService.bot.domain.Services;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;@Servicepublic class ServicesServiceImpl implements ServicesService {    private final ServicesRepository servicesRepository;    public ServicesServiceImpl(ServicesRepository servicesRepository) {        this.servicesRepository = servicesRepository;    }    @Override    public Page<Services> getServicePage(Pageable pageRequest, Specification<Services> servicesSpecification) {        return servicesRepository.findAll(servicesSpecification, pageRequest);    }}