package com.MITProjectService.bot.Services.impl;import com.MITProjectService.bot.Services.SnUserService;import com.MITProjectService.bot.dao.jpaRepos.UserRepository;import com.MITProjectService.bot.domain.User;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;@Servicepublic class SnUserServiceImpl implements SnUserService {    private final UserRepository userRepository;    public SnUserServiceImpl(UserRepository userRepository) {        this.userRepository = userRepository;    }    @Override    @Transactional    public void saveUser(User user) {        userRepository.save(user);    }}