package com.MITProjectService.bot.dao.jpaRepos;import com.MITProjectService.bot.domain.HiredServicesLog;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;@Repositorypublic interface HiredServiceLogRepository extends JpaRepository<HiredServicesLog, Long> , JpaSpecificationExecutor<HiredServicesLog> {    @Query("SELECT hsl FROM HiredServicesLog hsl WHERE hsl.booking.id = :id")    HiredServicesLog getByBookingId(@Param("id") Long id);}