package com.MITProjectService.bot.dao.jpaRepos;import com.MITProjectService.bot.domain.ServiceProvider;import jakarta.persistence.Table;import lombok.NonNull;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface ServiceProviderRepository extends JpaRepository<ServiceProvider, Long>, JpaSpecificationExecutor<ServiceProvider> {    @Query(value = "SELECT * FROM sn_user WHERE telegram_id = :telegramId", nativeQuery = true)    Optional<ServiceProvider> getBYTelegramId(@Param("telegramId") @NonNull Long telegramId);}