package com.MITProjectService.bot.dao.jpaRepos;import com.MITProjectService.bot.domain.ServiceProvider;import com.MITProjectService.bot.domain.ServiceRegistration;import com.MITProjectService.bot.domain.SnUser;import jakarta.persistence.Table;import lombok.NonNull;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface ServiceProviderRepository extends JpaRepository<ServiceProvider, Long>, JpaSpecificationExecutor<ServiceProvider> {    @Query("SELECT u FROM ServiceProvider u WHERE u.telegramId = :telegramId")    Optional<ServiceProvider> getUserByTelegramId(@Param("telegramId") Long telegramId);}