package com.MITProjectService.bot.dao.jpaRepos;import com.MITProjectService.bot.domain.ServiceRegistration;import io.lettuce.core.dynamic.annotation.Param;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;@Repositorypublic interface ServiceRegistrationRepository extends JpaRepository<ServiceRegistration, Long> , JpaSpecificationExecutor<ServiceRegistration> {    @Query("SELECT sr FROM ServiceRegistration sr WHERE sr.registeredBy.id = :providerId")    Page<ServiceRegistration> findByProviderId(@Param("registered_by") Long providerId, Pageable pageable);}