package com.MITProjectService.bot.dao.jpaRepos;import com.MITProjectService.admin.domain.system.SysUser;import com.MITProjectService.bot.domain.SnUser;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.domain.Specification;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.JpaSpecificationExecutor;import org.springframework.stereotype.Repository;@Repositorypublic interface UserRepository extends JpaRepository<SnUser, Long>, JpaSpecificationExecutor<SnUser> {    Page<SnUser> findAll(Specification<SnUser> specification, Pageable pageable);}