package com.MITProjectService.bot.dao.jpaRepos.specification;import com.MITProjectService.bot.domain.ServiceCategory;import com.MITProjectService.bot.request.ServiceCategoryRequest;import jakarta.persistence.criteria.Predicate;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;@Servicepublic class SnServiceCategorySpecificationFilter {    public static Specification<ServiceCategory> serviceCategoryNameFilter(ServiceCategoryRequest request) {        return (root, query, criteriaBuilder) -> {            Predicate predicate = criteriaBuilder.conjunction();            if (request.getCategoryName()!=null){                predicate = criteriaBuilder.and(predicate, criteriaBuilder.like(root.get("categoryName"), "%" + request.getCategoryName() + "%"));            }            return predicate;        };    }}