package com.MITProjectService.bot.domain;import jakarta.persistence.*;import lombok.Data;import java.io.Serializable;import java.util.Date;@Data@Entity@Table(name = "sn_user")public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(unique = true)    private Long telegramId;    @Column(nullable = false)    private String userName;    private String firstName;    private String lastName;    private String email;    @Column(unique = true)    private String phone;    @Temporal(TemporalType.TIMESTAMP)    private Date updateTime;    @Temporal(TemporalType.TIMESTAMP)    private Date registrationDate;    private String preferredLanguage;    private int status;    private int type;}