package com.MITProjectService.bot.domain.paymentGateWay;import com.MITProjectService.bot.domain.Payment;import com.fasterxml.jackson.annotation.JsonFormat;import jakarta.persistence.*;import lombok.Data;import java.time.LocalDateTime;@Data@Entity@Table(name = "pay_here_model")public class PayHereModel {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long id;    @Column(name = "order_id")    private String order_id;    @Column(name = "items")    private String items;    @Column(name = "currency")    private String currency;    @Column(name = "amount")    private Double amount;    @Column(name = "usee_id")    private Long userId;    @Column(name = "email")    private String email;    @Column(name = "phone")    private String phone;    @Column(name = "city")    private String city;    @Column(name = "country")    private String country;    @Column(name = "created_at", nullable = false, updatable = false)    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss")    private LocalDateTime createdAt;    @OneToOne    @JoinColumn(name = "payment_id", referencedColumnName = "id")    private Payment payment;    @PrePersist    protected void onCreate() {        createdAt = LocalDateTime.now();    }}