package com.MITProjectService.bot.enums;public enum BookingStatusEnum {    PENDING(1),    ACCEPTED(2),    REJECTED(3);    private final int value;    BookingStatusEnum(int value) {        this.value = value;    }    public int getValue() {        return value;    }    public static BookingStatusEnum fromValue(int value) {        for (BookingStatusEnum status : values()) {            if (status.value == value) {                return status;            }        }        throw new IllegalArgumentException("Invalid BookingStatus value: " + value);    }    public static BookingStatusEnum fromName(String name) {        try {            return BookingStatusEnum.valueOf(name.toUpperCase());        } catch (IllegalArgumentException e) {            throw new IllegalArgumentException("Invalid BookingStatus name: " + name, e);        }    }}