package com.MITProjectService.bot.enums;import lombok.AllArgsConstructor;import lombok.Getter;@AllArgsConstructor@Getterpublic enum PaymentGateWayEnum {    PAYHERE(1, "PAYHERE"), STRIPE(2, "STRIPE"), PENDING(0, "PENDING");    private final int value;    private final String name;    public static PaymentGateWayEnum fromValue(int value) {        for (PaymentGateWayEnum status : values()) {            if (status.value == value) {                return status;            }        }        throw new IllegalArgumentException("Invalid BookingStatus value: " + value);    }    public static PaymentGateWayEnum fromName(String name) {        try {            return PaymentGateWayEnum.valueOf(name.toUpperCase());        } catch (IllegalArgumentException e) {            throw new IllegalArgumentException("Invalid BookingStatus name: " + name, e);        }    }}