package com.MITProjectService.bot.enums;import lombok.AllArgsConstructor;import lombok.Getter;@Getter@AllArgsConstructorpublic enum PaymentMethodEnum {    CASH(1,"CASH"), CARD(2,"CARD"), ONLINE(3,"ONLINE"), PENDING(0,"PENDING" );    private final int value;    private final String name;    public static PaymentMethodEnum fromValue(int value) {        for (PaymentMethodEnum status : values()) {            if (status.value == value) {                return status;            }        }        throw new IllegalArgumentException("Invalid BookingStatus value: " + value);    }    public static PaymentMethodEnum fromName(String name) {        try {            return PaymentMethodEnum.valueOf(name.toUpperCase());        } catch (IllegalArgumentException e) {            throw new IllegalArgumentException("Invalid BookingStatus name: " + name, e);        }    }}