package com.MITProjectService.bot.enums;public enum PaymentServiceEnum {    PENDING(1),    PAID (2),    FAILED (3),    REFUNDED (4);    private final int value;    PaymentServiceEnum(int value) {        this.value = value;    }    public int getValue() {        return value;    }    public static PaymentServiceEnum fromValue(int value) {        for (PaymentServiceEnum status : values()) {            if (status.value == value) {                return status;            }        }        throw new IllegalArgumentException("Invalid BookingStatus value: " + value);    }    public static PaymentServiceEnum fromName(String name) {        try {            return PaymentServiceEnum.valueOf(name.toUpperCase());        } catch (IllegalArgumentException e) {            throw new IllegalArgumentException("Invalid BookingStatus name: " + name, e);        }    }    public static PaymentServiceEnum fromString(String status) {        for (PaymentServiceEnum paymentStatus : values()) {            if (paymentStatus.name().equalsIgnoreCase(status)) {                return paymentStatus;            }        }        throw new IllegalArgumentException("Invalid Booking string: " + status);    }}