package com.MITProjectService.bot.enums;public enum ServiceRegistrationEnum {    PENDING(1),    APPROVED(2),    REJECTED(3);    private final int val;    ServiceRegistrationEnum(int val) {        this.val = val;    }    public int getVal() {        return val;    }    public static ServiceRegistrationEnum fromValue(int value) {        for (ServiceRegistrationEnum status : values()) {            if (status.val == value) {                return status;            }        }        throw new IllegalArgumentException("Invalid RegistrationStatus value: " + value);    }    public static ServiceRegistrationEnum fromString(String status) {        for (ServiceRegistrationEnum registrationStatus : values()) {            if (registrationStatus.name().equalsIgnoreCase(status)) {                return registrationStatus;            }        }        throw new IllegalArgumentException("Invalid RegistrationStatus string: " + status);    }}