package com.MITProjectService.bot.request;import com.MITProjectCommon.constants.Constants;import jakarta.persistence.Column;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.Date;@Data@NoArgsConstructor@AllArgsConstructorpublic class SnUserRequest {    private int page=(int) Constants.DEFAULT_PAGE;    private int limit= (int) Constants.DEFAULT_LIMIT;    @Column(name = "id")    private Long id;    @Column(name = "telegram_id", unique = true)    private Long telegramId;    @Column(name = "user_name", nullable = false)    private String userName;    @Column(name = "first_name")    private String firstName;    @Column(name = "last_name")    private String lastName;    @Column(name = "email")    private String email;    @Column(name = "phone")    private String phone;    @Column(name = "update_time")    private Date updateTime;    @Column(name = "create_time")    private Date registrationDate;    @Column(name = "preferred_language")    private String preferredLanguage;    @Column(name = "status")    private int status;    @Column(name = "user_type")    private int type;    @Column(name = "category")    String category;    @Column(name = "location")    String location;}