package com.MITProjectService.exceptionhandling;import lombok.AllArgsConstructor;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.NoArgsConstructor;@EqualsAndHashCode(callSuper = true)@Data@AllArgsConstructor@NoArgsConstructorpublic class TokenRefreshException extends RuntimeException{    private String token;    public TokenRefreshException(String token, String message) {        super(message);        this.token = token;    }}