package com.MITProjectService.framework.redis;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.stereotype.Component;@Componentpublic class RedisCashService {    @Autowired    public RedisTemplate redisTemplate;    public <T> void set(final String key, final  T val){        redisTemplate.opsForValue().set(key, val);    }}