package com.MITProjectService.utils;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;@Servicepublic class TelegramBotWebHookURLSet {    @Value("${telegram.bot.token}")    private String botToken;    private final NgrokService ngrokService;    public TelegramBotWebHookURLSet(NgrokService ngrokService) {        this.ngrokService = ngrokService;        setWebhook();    }    private void setWebhook() {        String webhookUrl = ngrokService.getNgrokUrl() + "/app/telegram/message";        String telegramApiUrl = "https://api.telegram.org/bot"+botToken+"/setWebhook?url=" + webhookUrl;        RestTemplate restTemplate = new RestTemplate();        restTemplate.getForObject(telegramApiUrl, String.class);        System.out.println("Webhook set to: " + webhookUrl);    }}